package basic.dev;

public class MainApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 10, b = 5 ;
        //Phep cong
        System.out.println(a + b);
        //Phep tru
        System.out.println(a - b);
        //Phep nhan
        System.out.println(a * b);
        //Phep chia
        System.out.println(a / b);
        
        boolean c = a > b;
        System.out.println(c);
        
        c = a >= b;
        System.out.println(c);
        
        c = a < b;
        System.out.println(c);
        
        c = a <= b;
        System.out.println(c);
        
	}

}
